//---------------------------------------------------------------------------------------
bool AudioManager::InitHRTFs_()
{
    int const total_filter_samples_o3 = sizeof(google_omnitone_sh_hrir_o3)/sizeof(google_omnitone_sh_hrir_o3[0]);
    float hrir[total_filter_samples_o3];

    //
    // TBD : ask experters - adjust again if necessary!
    //
    float const gain  = 1.0f;
    float const scale = gain/32767.0f;

    // foa
    float* d = hrir;
    int const total_filter_samples_o1 = sizeof(google_omnitone_sh_hrir_o1)/sizeof(google_omnitone_sh_hrir_o1[0]);
    for (int i=0; i<total_filter_samples_o1; ++i) {
        *d++ = scale*google_omnitone_sh_hrir_o1[i];
    }
    foa_SH_HRTF_.Initialize(CONVOLUTION_BLOCK_SIZE, hrir, 4, 256);

    // soa
    d = hrir;
    int const total_filter_samples_o2 = sizeof(google_songbird_sh_hrir_o2)/sizeof(google_songbird_sh_hrir_o2[0]);
    for (int i=0; i<total_filter_samples_o2; ++i) {
        *d++ = scale*google_songbird_sh_hrir_o2[i];
    }
    soa_SH_HRTF_.Initialize(CONVOLUTION_BLOCK_SIZE, hrir, 9, 256);

    // toa
    d = hrir;
    for (int i=0; i<total_filter_samples_o3; ++i) {
        *d++ = scale*google_omnitone_sh_hrir_o3[i];
    }
    toa_SH_HRTF_.Initialize(CONVOLUTION_BLOCK_SIZE, hrir, 16, 256);

    // ITU 5.1/7.1
    d = hrir;
    int const total_filter_samples_itu_7_1 = sizeof(sadie_itu_7_1_hrir)/sizeof(sadie_itu_7_1_hrir[0]);
    for (int i=0; i<total_filter_samples_itu_7_1; ++i) {
        *d++ = scale*sadie_itu_7_1_hrir[i];
    }
    itu_7_1_HRTF_.Initialize(CONVOLUTION_BLOCK_SIZE, hrir, 14, 256);

    return true;
}

